import React, { useState, useEffect } from 'react';
import './LoginSignup.css';

function LoginSignup({ onLogin, onSignup, errorMessage }) {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    name: '', // Nuevo campo para el nombre
    email: '',
    password: '',
  });
  const [error, setError] = useState('');

  const [isSubmitting, setIsSubmitting] = useState(false); // Para manejar el estado de envío

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsSubmitting(true); // Cambiar estado a enviando

    try {
      if (isLogin) {
        // Intentar hacer login
        const response = await fetch('http://localhost:5000/login', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            email: formData.email,
            password: formData.password,
          }),
        });

        const data = await response.json();
        
        if (response.ok) {
          onLogin(data.user); // Pasa los datos del usuario al componente principal
          setError('');
        } else {
          setError('Correo o contraseña incorrectos');
        }
      } else {
        // Intentar hacer registro
        const response = await fetch('http://localhost:5000/register', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            name: formData.name,
            email: formData.email,
            password: formData.password,
          }),
        });

        const data = await response.json();

        if (response.ok) {
          onSignup(data.user);
          setError('');
          setIsLogin(true);
          setFormData({ name: '', email: '', password: '' });
        } else {
          setError('Este correo ya está registrado');
        }
      }
    } catch (err) {
      console.error('Error:', err);
      setError('Hubo un error en la conexión con el servidor');
    } finally {
      setIsSubmitting(false); // Termina el estado de envío
    }
  };

  const isEmailValid = /\S+@\S+\.\S+/.test(formData.email);

  return (
    <div className="container">
      <div className="form-container">
        <h2>{isLogin ? 'Biblioteca Virtual' : 'Registrarse'}</h2>
        <form onSubmit={handleSubmit}>
          {!isLogin && (
            <div className="input-group">
              <input
                type="text"
                id="name"
                name="name"
                required={!isLogin} 
                value={formData.name}
                onChange={handleInputChange}
                placeholder=" "
              />
              <label htmlFor="name">Nombre</label>
            </div>
          )}
          <div className="input-group">
            <input
              type="email"
              id="email"
              name="email"
              required
              value={formData.email}
              onChange={handleInputChange}
              placeholder=" "
            />
            <label htmlFor="email">Correo electrónico</label>
            {!isEmailValid && formData.email.length > 0 && (
              <span className="error-text">Por favor, ingresa un correo válido</span>
            )}
          </div>
          <div className="input-group">
            <input
              type="password"
              id="password"
              name="password"
              required
              value={formData.password}
              onChange={handleInputChange}
              placeholder=" "
            />
            <label htmlFor="password">Contraseña</label>
          </div>
          {error && <div className="error-message">{error}</div>}
          <input
            type="submit"
            value={isLogin ? 'Iniciar sesión' : 'Registrarse'}
            disabled={
              (!isLogin && (formData.name.length === 0 || formData.password.length === 0)) ||
              !isEmailValid ||
              formData.password.length === 0 ||
              isSubmitting
            }
          />
        </form>
        <p>
          {isLogin ? '¿No tienes una cuenta? ' : '¿Ya tienes cuenta? '}
          <span
            className="link"
            onClick={() => setIsLogin(!isLogin)}
          >
            {isLogin ? 'Regístrate' : 'Inicia sesión'}
          </span>
        </p>
      </div>
    </div>
  );
}

export default LoginSignup;
