import React, { useState } from 'react';
import './LoginSignup.css';

function LoginSignup() {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
  });
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setSuccessMessage('');

    try {
      const response = await fetch(
        isLogin ? '/api/auth/login' : '/api/auth/register',
        {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(formData),
        }
      );

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.message || 'Ocurrió un error');
      }

      if (isLogin) {
        // Guardar el token en localStorage (si el backend lo proporciona)
        localStorage.setItem('token', data.token);
        setSuccessMessage('Inicio de sesión exitoso.');
      } else {
        setSuccessMessage('Registro exitoso. Ahora puedes iniciar sesión.');
        setIsLogin(true);
      }
    } catch (error) {
      setError(error.message);
    }
  };

  // Validación simple para el correo
  const isEmailValid = /\S+@\S+\.\S+/.test(formData.email);

  return (
    <div className="container">
      <div className="form-container">
        <h2>{isLogin ? 'Iniciar Sesión' : 'Registrarse'}</h2>
        <form onSubmit={handleSubmit}>
          {!isLogin && (
            <div className="input-group">
              <input
                type="text"
                id="name"
                name="name"
                value={formData.name}
                onChange={handleInputChange}
                placeholder=" "
                required={!isLogin}
              />
              <label htmlFor="name">Nombre</label>
            </div>
          )}
          <div className="input-group">
            <input
              type="email"
              id="email"
              name="email"
              value={formData.email}
              onChange={handleInputChange}
              placeholder=" "
              required
            />
            <label htmlFor="email">Correo electrónico</label>
            {!isEmailValid && formData.email.length > 0 && (
              <span className="error-text">Por favor, ingresa un correo válido</span>
            )}
          </div>
          <div className="input-group">
            <input
              type="password"
              id="password"
              name="password"
              value={formData.password}
              onChange={handleInputChange}
              placeholder=" "
              required
            />
            <label htmlFor="password">Contraseña</label>
          </div>
          {error && <div className="error-message">{error}</div>}
          {successMessage && (
            <div className="success-message">{successMessage}</div>
          )}
          <input
            type="submit"
            value={isLogin ? 'Iniciar Sesión' : 'Registrarse'}
            disabled={
              (!isLogin && (formData.name.length === 0 || formData.password.length === 0)) ||
              !isEmailValid ||
              formData.password.length === 0
            }
          />
        </form>
        <p>
          {isLogin ? '¿No tienes una cuenta? ' : '¿Ya tienes una cuenta? '}
          <span
            className="link"
            onClick={() => {
              setIsLogin(!isLogin);
              setError('');
              setSuccessMessage('');
            }}
          >
            {isLogin ? 'Regístrate' : 'Inicia sesión'}
          </span>
        </p>
      </div>
    </div>
  );
}

export default LoginSignup;
