import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate, useNavigate, useLocation } from 'react-router-dom';
import './App.css';
import LoginSignup from './LoginSignup';
import { FaEnvelope, FaWhatsapp, FaUser } from 'react-icons/fa';

const initialBooks = [
  { title: 'Cien años de soledad', author: 'Gabriel García Márquez', summary: 'Cien años de soledad de Gabriel García Márquez cuenta la historia de la familia Buendía en el pueblo ficticio de Macondo...', category: 'Ficción', image: '/book1.jpg', reviews: [] },
  { title: 'El principito', author: 'Antoine de Saint-Exupéry', summary: 'El principito de Antoine de Saint-Exupéry cuenta la historia de un niño que viaja por planetas...', category: 'Fábula', image: '/book2.jpg', reviews: [] },
  { title: 'Lady Masacre', author: 'Mario Mendoza', summary: 'Lady Masacre de Mario Mendoza sigue a un hombre obsesionado con encontrar respuestas sobre el pasado...', category: 'Ficcion', image: '/book3.jpg', reviews: [] },
  { title: 'Don Quijote de la Mancha', author: 'Miguel de Cervantes', summary: 'Don Quijote de la Mancha de Miguel de Cervantes cuenta las aventuras de un caballero loco...', category: 'Clásico', image: '/book4.jpg', reviews: [] },
  { title: 'Más allá de las estrellas', author: 'Matt Dyamond', summary: 'Más allá de las estrellas de Matt Diamond narra la historia de un joven...', category: 'Ciencia ficción', image: '/book5.jpg', reviews: [] },
  { title: 'Atlantis: Proyecto Tarsis', author: 'José Enrique Serrano Expósito', summary: 'Atlantis: Proyecto Tarsis de Enrique Serrano Expósito sigue a un grupo de científicos...', category: 'Ciencia ficción', image: '/book6.jpg', reviews: [] },
  { title: 'A dos centímetros de ti', author: 'Elizabeth Eulberg', summary: 'A dos centímetros de ti de Elizabeth Eulberg narra la historia de dos adolescentes...', category: 'Romántico', image: '/book7.jpg', reviews: [] },
  { title: 'Antes de ti', author: 'Jojo Moyes', summary: 'Antes de ti de Jojo Moyes cuenta la historia de Louisa...', category: 'Romántico', image: '/book8.jpg', reviews: [] },
  { title: 'A través de mi ventana', author: 'Ariana Godoy', summary: 'A través de mi ventana de Ariana Godoy narra la historia de Raquel...', category: 'Romántico', image: '/book9.jpg', reviews: [] },
];

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [currentUser, setCurrentUser] = useState(null);
  const [books, setBooks] = useState(() => {
    const storedBooks = JSON.parse(localStorage.getItem('books'));
    return storedBooks || initialBooks;
  });
  const [selectedBook, setSelectedBook] = useState(null);
  const [filter, setFilter] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    localStorage.setItem('books', JSON.stringify(books));
  }, [books]);

  const handleLogin = (userData) => {
    setIsAuthenticated(true);
    setCurrentUser(userData.name);
  };

  const handleSignup = (userData) => {
    setIsAuthenticated(false);
    setCurrentUser(null);
    alert('Registro exitoso, por favor inicia sesión.');
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setCurrentUser(null);
  };

  const handleBookClick = (book) => {
    setSelectedBook(book);
    navigate('/book-details', { state: { book } });
  };

  const updateBookReviews = (updatedBook) => {
    setBooks(books.map(book => (book.title === updatedBook.title ? updatedBook : book)));
  };

  const handleFilterChange = (e) => {
    setFilter(e.target.value);
  };

  const filteredBooks = filter
    ? books.filter(book =>
        book.title.toLowerCase().includes(filter.toLowerCase()) ||
        book.author.toLowerCase().includes(filter.toLowerCase()) ||
        book.category.toLowerCase().includes(filter.toLowerCase())
      )
    : books;

  const handleBackToList = () => {
    setSelectedBook(null);
    navigate('/');
  };

  return (
    <div className="App">
      {isAuthenticated ? (
        <div>
          <h1>Bienvenido a Tu Biblioteca Virtual</h1>
          <p>Usuario: {currentUser}</p>
          <button onClick={handleLogout}>Cerrar sesión</button>

          <Routes>
            <Route
              path="/"
              element={
                <div>
                  {!selectedBook && (
                    <input
                      type="text"
                      placeholder="Filtrar por título, autor o categoría"
                      value={filter}
                      onChange={handleFilterChange}
                    />
                  )}
                  <BookList books={filteredBooks} onBookClick={handleBookClick} />
                </div>
              }
            />
            <Route
              path="/book-details"
              element={<BookDetails currentUser={currentUser} onUpdateBook={updateBookReviews} onBackToList={handleBackToList} />}
            />
            <Route path="*" element={<Navigate to="/" />} />
          </Routes>

          {/* Información de contacto */}
          <footer className="contact-info">
            <h3>Información de Contacto</h3>
            <p><FaUser /> Nombre: Brayan David Gordo Naranjo</p>
            <p><FaEnvelope /> Correo: gordonaranjo123@gmail.com</p>
            <p><FaWhatsapp /> Celular: 314 4473755</p>
            <p>Ingeniero Electrónico</p>
          </footer>
        </div>
      ) : (
        <LoginSignup onLogin={handleLogin} onSignup={handleSignup} />
      )}
    </div>
  );
}

function BookList({ books, onBookClick }) {
  return (
    <div className="book-list">
      {books.map((book, index) => (
        <div key={index} className="book-item" onClick={() => onBookClick(book)}>
          <img src={book.image} alt={book.title} className="book-image" />
          <h3>{book.title}</h3>
          <p><strong>Autor:</strong> {book.author}</p>
          <p><strong>Categoría:</strong> {book.category}</p>
        </div>
      ))}
    </div>
  );
}

function BookDetails({ currentUser, onUpdateBook, onBackToList }) {
  const location = useLocation();
  const { book } = location.state || {};
  const [rating, setRating] = useState('');
  const [comment, setComment] = useState('');
  const [reviews, setReviews] = useState(book?.reviews || []);
  const [editMode, setEditMode] = useState(null);

  const handleAddReview = () => {
    if (!rating || isNaN(rating) || rating < 1 || rating > 5) {
      alert('La calificación debe ser un número entre 1 y 5.');
      return;
    }
    if (!comment.trim()) {
      alert('El comentario no puede estar vacío.');
      return;
    }

    const newReview = { user: currentUser, rating, comment, timestamp: new Date().toISOString() };
    const updatedReviews = [newReview, ...reviews].sort((a, b) => new Date(b.timestamp) - new Date(a.timestamp));
    setReviews(updatedReviews);

    const updatedBook = { ...book, reviews: updatedReviews };
    onUpdateBook(updatedBook);

    setRating('');
    setComment('');
  };

  const handleEditReview = (index) => {
    const review = reviews[index];
    setRating(review.rating);
    setComment(review.comment);
    setEditMode(index);
  };

  const handleSaveEditedReview = () => {
    if (!rating || isNaN(rating) || rating < 1 || rating > 5) {
      alert('La calificación debe ser un número entre 1 y 5.');
      return;
    }
    if (!comment.trim()) {
      alert('El comentario no puede estar vacío.');
      return;
    }

    const updatedReviews = [...reviews];
    updatedReviews[editMode] = { user: currentUser, rating, comment };
    setReviews(updatedReviews);

    const updatedBook = { ...book, reviews: updatedReviews };
    onUpdateBook(updatedBook);

    setRating('');
    setComment('');
    setEditMode(null);
  };

  const handleDeleteReview = (index) => {
    const updatedReviews = reviews.filter((_, i) => i !== index);
    setReviews(updatedReviews);

    const updatedBook = { ...book, reviews: updatedReviews };
    onUpdateBook(updatedBook);
  };

  if (!book) {
    return <Navigate to="/" />;
  }

  return (
    <div>
      <button onClick={onBackToList}>Volver a la lista</button>
      <h2>{book.title}</h2>
      <img src={book.image} alt={book.title} />
      <p>{book.summary}</p>
      <h3>Reseñas:</h3>
      <div>
        {reviews.length > 0 ? (
          reviews.map((review, index) => (
            <div key={index} className="review">
              <p><strong>Usuario:</strong> {review.user}</p>
              <p><strong>Calificación:</strong> {review.rating}</p>
              <p><strong>Comentario:</strong> {review.comment}</p>
              <button onClick={() => handleEditReview(index)}>Editar</button>
              <button onClick={() => handleDeleteReview(index)}>Eliminar</button>
            </div>
          ))
        ) : (
          <p>No hay reseñas aún.</p>
        )}
      </div>
      <h3>Deja tu reseña:</h3>
      <div>
        <label>Calificación (1-5):</label>
        <input type="number" min="1" max="5" value={rating} onChange={(e) => setRating(e.target.value)} />
      </div>
      <div>
        <label>Comentario:</label>
        <textarea value={comment} onChange={(e) => setComment(e.target.value)} />
      </div>
      {editMode !== null ? (
        <button onClick={handleSaveEditedReview}>Guardar cambios</button>
      ) : (
        <button onClick={handleAddReview}>Añadir reseña</button>
      )}
    </div>
  );
}

export default App;
